pragma solidity ^0.8.0;

// This file allows the user to import all Sphinx contracts and data structures from a single file
// instead of needing to find the exact location of the file they need to import. We currently only
// import items that we have mentioned in the user-facing documentation.

import { Sphinx } from "./Sphinx.sol";
